contracts/SAFTDestinationMinter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import { CCIPReceiver } from "@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol";
import { Client } from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import { SAFTToken } from "./SAFTToken.sol";

contract SAFTDestinationMinter is CCIPReceiver {
	SAFTToken saft;

	event MintCallSuccessfull();

	constructor(address router, address saftAddress) CCIPReceiver(router) {
		saft = SAFTToken(saftAddress);
	}

	function _ccipReceive(
		Client.Any2EVMMessage memory message
	) internal override {
		(bool success, ) = address(saft).call(message.data);
		require(success);
		emit MintCallSuccessfull();
	}
}

contracts/SAFTSourceMinter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import { LinkTokenInterface } from "@chainlink/contracts/src/v0.8/shared/interfaces/LinkTokenInterface.sol";
import { IRouterClient } from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import { Client } from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";

/**
 * THIS IS AN EXAMPLE CONTRACT THAT USES HARDCODED VALUES FOR CLARITY.
 * THIS IS AN EXAMPLE CONTRACT THAT USES UN-AUDITED CODE.
 * DO NOT USE THIS CODE IN PRODUCTION.
 */
contract SAFTSourceMinter {
	enum PayFeesIn {
		Native,
		LINK
	}

	address immutable i_router;
	address immutable i_link;

	event MessageSent(bytes32 messageId);

	constructor(address router, address link) {
		i_router = router;
		i_link = link;
		LinkTokenInterface(i_link).approve(i_router, type(uint256).max);
	}

	receive() external payable {}

	function mint(
		uint256 amount,
		bytes32[] calldata merkleProof,
		uint64 destinationChainSelector,
		address receiver,
		PayFeesIn payFeesIn
	) external {
		Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
			receiver: abi.encode(receiver),
			data: abi.encodeWithSignature(
				"mint(address,uint256,bytes,bytes32[])",
				msg.sender,
				amount,
				"",
				merkleProof
			),
			tokenAmounts: new Client.EVMTokenAmount[](0),
			extraArgs: "",
			feeToken: payFeesIn == PayFeesIn.LINK ? i_link : address(0)
		});

		uint256 fee = IRouterClient(i_router).getFee(
			destinationChainSelector,
			message
		);

		bytes32 messageId;

		if (payFeesIn == PayFeesIn.LINK) {
			// LinkTokenInterface(i_link).approve(i_router, fee);
			messageId = IRouterClient(i_router).ccipSend(
				destinationChainSelector,
				message
			);
		} else {
			messageId = IRouterClient(i_router).ccipSend{ value: fee }(
				destinationChainSelector,
				message
			);
		}

		emit MessageSent(messageId);
	}
}

