pragma solidity 0.8.19;

import { CCIPReceiver } from "@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol";
import { Client } from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import { SAFTToken } from "./SAFTToken.sol";

contract SAFTDestinationMinter is CCIPReceiver {
	SAFTToken saft;

	event MintCallSuccessfull();

	constructor(address router, address saftAddress) CCIPReceiver(router) {
		saft = SAFTToken(saftAddress);
	}

	function _ccipReceive(
		Client.Any2EVMMessage memory message
	) internal override {
		(bool success, ) = address(saft).call(message.data);
		require(success);
		emit MintCallSuccessfull();
	}
}
